class Solution {
public:
    int maxOperations(string s) {
        int count = 0;
        int ans = 0;
        for(int i = s.length()-1;i>0;i--){
            if(s[i] == '1'){
                ans += count;
            }
            else if(s[i] == '0' && s[i-1] == '1'){
                count++;
            }
        }
        if(s[0] == '1') ans+=count;
        return ans;
    }
};
