class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int i=0,ans=0,c1=0;
        while(i<n){
            while(i<n && s[i]=='0') i++;
            while(i<n && s[i]=='1') {
                c1++;
                i++;
            }
            if(i<n && s[i]=='0') ans+=c1;
            cout<<endl;
        }
        return ans;
    }
};
