class Solution {
public:
    int maxOperations(string s) {
        int count {} ;
        int ones {} ;
        int i {} ;
        int n = s.length() ;
        while(i < n){
            if(s[i] == '1') ones++ ;
            else {
                while(i+1 < n && s[i+1] == '0' ) i++ ;
                count += ones ;
            }
            i++ ;
        }

        return count ;
    }
};
