class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();
        int cnt=0;
        long long ans=0;

        for(int i=n-1;i>=0;i--)
        {
           if(s[i]=='0')
           {
               if(cnt==0)
               {
                   cnt+=1;
               }
               else if(i+1<n && s[i+1]=='1')
               {
                   cnt+=1;
               }
           }
            else 
            {
                ans+=cnt;
            }

        }
        return ans;
    }
};
