class Solution {
public:
  int maxOperations(string s) {
    int n = (int)s.size();
    int one = 0, zero = 0;
    int ans = 0;
    for (auto c:s) {
      if (c == '1') {
        if (zero >= 1) {
          ans += one;
          zero = 0;
        }
        one++;
      } else
        zero++;
    }

     if(s[n-1]=='0'){
         if (zero >= 1) {
          ans += one;
          zero = 0;
        }
     }

      return ans;
  }
};
