class Solution:
    def maxOperations(self, s: str) -> int:
        ans=0
        cnd=0
        count=0
        for i in range(len(s)):
            if s[i]=='1':
                count+=1
                cnd=1
            if s[i]=='0' and cnd==1:
                ans+=count
                cnd=0
        return ans
