class Solution {
public:
    int maxOperations(string s) {

        vector<int> arr;
        int zeros=0, ones=0;
        for(int i=0; i<s.length(); i++) {
            if(s[i]=='0') {
                if(ones!=0) {
                    arr.push_back(ones);
                    ones=0;
                }
                zeros--;
            }else{
                if(zeros!=0) {
                    arr.push_back(zeros);
                    zeros = 0;
                }
                ones++;
            }
        }
        if(ones!=0) arr.push_back(ones);
        if(zeros!=0) arr.push_back(zeros);
        int count = 0;

        for(int i=0; i<arr.size()-1; i++) {
            if(arr[i]>0 && arr[i+1] < 0) {
                count += arr[i];
                if(i+2 < arr.size()) {
                    arr[i+2] += arr[i];
                }
            }
        }

        return count;

    }
};
