class Solution {
public:
    int maxOperations(string s) {
        int a = 0;
        int c=0;
        bool k = false;
        for(int i=0;i<s.length();i++){
            if(s[i] == '1'){
                c++;
                k=true;
            }else if(s[i]=='0' && k){
                a += c;
                k = false;
            }
            // cout<<a;
        }
        return a;
    }
};
