def RLE(s):
    kk=[]
    vv=[]
    for c in s:
        if kk and kk[-1]==c:
            vv[-1]+=1
        else:
            kk.append(c)
            vv.append(1)
    return kk,vv

class Solution:
    def maxOperations(self, s: str) -> int:
        kk,vv=RLE(s)
        now=ans=0
        n=len(kk)
        for i in range(n)[::-1]:
            k,v=kk[i],vv[i]
            if k=="1":
                ans+=now*v
            else:
                now+=1
        return ans
