class Solution:
    def maxOperations(self, s: str) -> int:
        res = 0
        clumped = 0
        scored = False
        for i in range(len(s)):
            if s[i] == '1':
                scored = False
                clumped += 1
            else:
                if not scored:
                    scored = True
                    res += clumped
                else:
                    continue
        return res
