class Solution {
public:
    int maxOperations(string s) {
        int rs = 0, on = 0;
        int chk = 1;
        int i = 0;
        while (i < s.size()) {
            chk = processChar(s[i], on, chk, rs);
            i++;
        }
        return rs;
    }

private:
    int processChar(char c, int &on, int chk, int &rs) {
        return (c == '1') ? (on++, 1) : (chk ? (rs += on, 0) : chk);
    }
};
