class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int p = 0;
        bool has_one = false;
        vector<int> bits;

        for (char c : s) {
            if (c == '1') {
                bits.push_back(1);
                has_one = true;
            } else if (c == '0' && has_one) {
                bits.push_back(0);
                has_one = false;
            }
        }

        for (int bit : bits) {
            if (bit == 1) {
                p++;
            } else {
                ans += p;
            }
        }

        return ans;
    }
};
