class Solution {
public:
    int maxOperations(string s) {
          int n = s.size();
          int ans = 0;
          int one = 0;
          int ok = 1;
          for(int  i= 0; i< n; i++){
            if(s[i] == '0'){
                if(ok == 1) ans+=one;
                ok = 0;
            }
            else{
                ok = 1;
                one++;
            }
          }
          return ans;
    }
};
