class Solution:
    def maxOperations(self, s: str) -> int:
        arr = []
        for i, it in enumerate(s):
            if it == '1':
                arr.append(i)
        arr.append(len(s))
        cnt = 0
        for i in range(1, len(arr)):
            if arr[i]- arr[i-1] > 1:
                cnt += i
        return cnt
