class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, temp = 0;
        bool flag = true;
        for (int i = 0; i < s.size(); i++) {
            if (s[i] == '1') {
                temp++;
                flag = true;
            } else {
                if (flag==true) ans += temp;
                flag = false;
                }
            }
        return ans;
    }
};
