class Solution {
public:
    int maxOperations(string s) {

        int ans=0;
        int n=s.length();
        int ct=0;
        for(int i=0 ; i<n-1 ; i++){
            if(s[i]=='1'){
                ct++;
                if(s[i+1]=='0'){
                    ans+=ct;
                }
            }
        }
        return ans;
    }
};
