class Solution {
public:
    int maxOperations(string s) {
        s.push_back('1');
        int n=s.size();
        bool f=0;
        int cnt=0,ans=0;
        for(int i=0;i<n;i++){
            if(s[i]=='0') f=1;
            else if(s[i]=='1'){
                if(f) ans+=cnt;
            }
            if(s[i]=='1') cnt++,f=0;
        }
        return ans;
    }
};
