public class Solution {
        public int MaxOperations(string s)
        {
            int cnt = s[^1] == '0' ? 1 : 0;
            int ans = 0;
            for (int i = s.Length - 1; i >= 0; i--)
            {
                if (s[i] == '0')
                    continue;
                int j = i;
                while (j - 1 >= 0 && s[j] == s[j - 1])
                {
                    j--;
                }
                ans += (i - j + 1) * cnt;
                i = j;
                cnt++;
            }
            return ans;
        }
}
