class Solution {
public:
    int maxOperations(string s) {
        int i =0, n= s.size(), one =0, ans=0;
        while(i<n){
            while(i<n && s[i]=='1')
                one++,i++;
            int zero =0;
            while(i<n && s[i]=='0')
                i++, zero++;
            if(zero>0)
            ans = ans+one;
            //cout<<" ans "<<ans<<" one "<<one<<" i "<<i<<endl;
        }
        return ans;
    }
};
