class Solution {
    public int maxOperations(String s) {
        List<Integer> list = new ArrayList<>();
        List<Integer> len = new ArrayList<>();
        int i = 0;
        while(i<s.length()){
            if(s.charAt(i) == '1'){
                int j = i+1;
                while(j<s.length() && s.charAt(j) == '1'){
                    j++;
                }
                list.add(i);
                len.add(j-i);
                i = j;
            }
            else{
                i++;
            }
        }
        if(list.size() == 0){
            return 0;
        }
        if(list.get(list.size()-1) + len.get(list.size()-1) == s.length()){
            list.remove(list.size()-1);
            len.remove(len.size()-1);
        }
        if(list.size() == 0){
            return 0;
        }
        // System.out.println(list);
        // System.out.println(len);
        int index = 0, res = 0;
        while(index < len.size()-1){
            res+=len.get(index);
            len.set(index+1, len.get(index+1) + len.get(index));
            index++;
        }
        res+=len.get(index);
        return res;
    }
}
