class Solution {
public:
    int maxOperations(string s) {
        string t=s;
        sort(t.begin(), t.end());
        if (s==t) return 0;

        char cur=s[0];
        int cnt=0;
        vector<char> crr;
        vector<int> arr;
        for (auto c: s)
            if (c==cur)
                cnt++;
            else{
                arr.push_back(cnt);
                crr.push_back(cur);
                cur=c;
                cnt=1;
            }
        arr.push_back(cnt);
        crr.push_back(cur);

        // for (auto c: arr) cout << c << " "; cout << endl;
        // for (auto c: crr) cout << c << " "; cout << endl;
        // cout << endl;

        int ret=0, n=arr.size();
        for (int i=0; i<n-1; i++){
            if (crr[i]=='1' && crr[i+1]=='1'){
                arr[i+1]+=arr[i];
                arr[i]=0;
            }
            else if (crr[i]=='0' && crr[i+1]=='0'){
                arr[i+1]+=arr[i];
                arr[i]=0;
            }
            else if (crr[i]=='1' && crr[i+1]=='0'){
                ret+=arr[i];
                swap(crr[i], crr[i+1]);
                swap(arr[i], arr[i+1]);
            }
            // for (auto c: arr) cout << c << " "; cout << endl;
            // for (auto c: crr) cout << c << " "; cout << endl;
            // cout << ret << endl;
        }

        return ret;
    }
};
