class Solution {
public:
    int maxOperations(const std::string& s) {
        int ans = 0, ones = 0;
        bool lastOne = false;
        for (char c : s) {
            if (c == '1') {
                ones++;
                lastOne = true;
            } else if (lastOne) {
                ans += ones;
                lastOne = false;
            }
        }
        return ans;
    }
};
