class Solution:

    def maxOperations(self, s: str) -> int:


        ones=0

        ans  = 0 
        s+="1"
        flg=0

        for x in s:
            if x=="1" and flg==1:
                ans+=ones
                ones+=1
                flg=0
            elif x=='0':
                flg=1
            elif x=="1":
                ones+=1


        return ans
