class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int cnt = 0;
        int n = s.size();
        if(s[0] == '1') cnt++;
        int i=0;
        while(i<n){
            while(i+1 <= n-1 && s[i+1] == '0') i++;
            if(s[i] == '0')ans+=cnt;
            while(i+1<=n-1 && s[i+1] == '1') cnt++,i++;
            i++;
        }
        return ans;
    }
};
