class Solution {
public:
    int maxOperations(string s) {
        int cnt=0,ans=0;
        bool flg=false;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){

                if(flg)ans+=cnt;
                flg=false;
                cnt++;
            }
            else flg=true;
        }
        if(s[s.size()-1]=='0')ans+=cnt;
        return ans;
    }
};
