class Solution {
public:
    int maxOperations(string s) {
        int last = 0;
        int res = 0;
        for (int i = 0; i < s.length(); i++) {
            if (i > 0 && s[i] == '1') {
                if (s[i - 1] == '0') {
                    res += last;
                }
            } else if (i == s.length() - 1) {
                res += last;
            }
            if (s[i] == '1') {
                last++;
            }
        }
        return res;
    }
};
