class Solution {
public:
    int maxOperations(string s) {
        int result = 0;
        int onesCount = 0;
        bool foundOne = false;
        vector<int> pattern;

        int i = 0;
        while (i < s.length()) {
            if (s[i] == '0' && foundOne) {
                pattern.push_back(0);
                foundOne = false;
            }
            else if (s[i] == '1') {
                pattern.push_back(1);
                foundOne = true;
            } 
            i++;
        }

        int j = 0;
        while (j < pattern.size()) {
            if (pattern[j] != 1) {
                result += onesCount;

            } else {
                onesCount++;
            }
            j++;
        }

        return result;
    }
};
