class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int c = 0;
        bool flag = false;

        for (char ch : s) {
            if (ch == '1') {
                c++;
                flag = true;
            } else if (flag) {
                ans += c;
                flag = false;
            }
        }

        return ans;
    }
};
