class Solution {
public:
    int maxOperations(string s) {
        int n=s.size(),ans=0,cnt=0;
        reverse(s.begin(),s.end());
       for(int i=0;i<n;i++){
           if(s[i]=='1'){
                ans+=cnt;
           }
           else{
              if(i-1>=0 and s[i-1]==s[i]) continue;
            else if(i==0) cnt++;
              else if(s[i-1]!=s[i]) cnt++;
           }
       }
        return ans;
    }
};
