class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int one=0;
        int flag=0;
        int n=s.size();
        for(int i=0;i<n;i++){
            if(s[i]=='0'){
                flag=1;
                continue;
            }
            if(flag){
                flag=0;
                ans+=one;
            }
            one+=(s[i]-'0');
        }
        if(flag){
            ans+=one;
        }
        return ans;
    }
};
