class Solution:
    def mergeZeroes(self, s: str) -> str:
        res = ""
        for c in s:
            if not (len(res) > 0 and res[-1] == '0' and c == '0'):
                res += c
        return res

    def maxOperations(self, s: str) -> int:
        s = self.mergeZeroes(s)
        ones = 0
        res = 0
        for c in s:
            if c == '1':
                ones += 1
            else:
                res += ones
        return res
