class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        int cnt = 0, ans = 0;
        for (int i = 0; i < n; i++) {
            if (s[i] == '1') {
                if (cnt) {
                    if (s[i - 1] == '0') {
                        ans += cnt;
                    }
                }
                cnt++;
            }
        }
        if (s[n - 1] == '0') ans += cnt;
        return ans;
    }
};
