class Solution:
    def maxOperations(self, s: str) -> int:
        l = list(s)
        while l and l[-1] == '1':
            l.pop()
        s = "".join(l)
        arr = []
        cnt = 0
        for ch in s:
            if ch == '1':
                cnt += 1
            elif cnt != 0:
                arr.append(cnt)
                cnt = 0
        if cnt != 0:
            arr.append(cnt)
        ret = 0
        cnt = 0
        for x in arr:
            cnt += x
            ret += cnt
        return ret
