class Solution
{
public:
    int maxOperations(string s)
    {
        int n = s.length(), ans = 0, c = 0;
        bool one = false;
        vector<int> cache;
        for (char ch : s)
        {
            if (ch == '0' && one)
            {
                cache.push_back(0);
                one = false;
            }
            else if (ch == '1')
            {
                cache.push_back(1);
                one = true;
            }
        }

        for (int v : cache)
        {
            if (v)
                ++c;
            else
                ans += c;
        }
        return ans;
    }
};
