#include <vector>
#include <string>
using namespace std;

class Solution { 
public: 
    int maxOperations(string s) { 
        int result = 0, onesCount = 0; 
        bool foundOne = false; 
        vector<int> pattern; 

        for (int i = 0; i < s.size(); i++) { 
            if (s[i] == '1') { 
                pattern.push_back(1); 
                foundOne = true; 
            } 
            if (s[i] == '0' && foundOne == true) { 
                pattern.push_back(0); 
                foundOne = false; 
            } 
        } 

        for (auto element : pattern) { 
            if (element == 1) { 
                onesCount++; 
            } else { 
                result += onesCount; 
            } 
        } 

        return result; 
    } 
};
