class Solution {
    public int maxOperations(String s) {
        char[] c=s.toCharArray();
        int cnt1=0;
        int cnt0=0;
        int cnt=0;
  int i=0;
    boolean a=false;
        while(i<c.length){
            if(c[i]=='0' && a){
                i++;
                continue;
            }
            if(c[i]=='1'){
                cnt1++;
                a=false;
            }
            if(c[i]=='0'){
                cnt+=cnt1;
                a=true;

            }
            i++;
        }

        return cnt;
    }
}
