class Solution {
    public int maxOperations(String s) {
        int ct = 0;
        int cur = 0;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i)=='1') {
                if (i!=0 && s.charAt(i-1)!='1') {
                ct += cur;
                }
                cur++;
            }
        }
        if (s.charAt(s.length()-1)!= '1') ct += cur;
        return ct;
    }
}
