class Solution {
public:
    int maxOperations(string s) {
        int o=0;
        int n=s.size();
        int ans=0;
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                o++;
            }else if(s[i]=='0'){
                while(s[i+1]=='0' && i<n)i++;
                // cout<<i<<" "<<o<<" fa"<<endl;
                ans+=o;
            }
        }
        // if(s[n-1]=='0')
        return ans;
    }
};
