class Solution {
public:
    int maxOperations(string s) {
        int one=0;
        int zero=0,op=0;
        bool iszero=0;
        for(int i=0;i<s.size();i++)
        {
            if(s[i]=='1')
            {
                iszero=true;
                one++;
            }
            else if(one && iszero)
            {
                iszero=false;
                op+=one;
            }
        }
        return op;
    }
};
