class Solution {
public:
    int maxOperations(string s) {
        vector<int> bit;
        int q = 0;
        bool has1 = false;
        int operation = 0;

        for(char ch: s){
            if(ch == '1'){
                bit.push_back(1);
                has1 = true;
            }else if(ch == '0' && has1){
                bit.push_back(0);
                has1 = false;
            }

        }
        for(int bits: bit){
            if(bits == 1){
                q++;
            }else{
                operation += q;
            }
        }
        return operation;
    }
};
