class Solution {
public:
    int maxOperations(string s) {
        string str;
        str.push_back(s.front());
        for(int i = 1;i<s.size();i++){
            if(str.back() == '0' and s[i] == '0')continue;
            str.push_back(s[i]);
        };
        long long int cnt = 0,one = 0;
        for(auto ch:str){
            one+=ch == '1';
            if(ch == '0')cnt+=one;
        };
        return cnt;
    }
};
