class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int count = 0;
        bool flag = false;
        for(int i = 0;i < s.length();i++) {
            if(s[i] == '1') {
                // if(flag == true)
                    // ans += count;
                count++;
                flag = false;
            }
            else{
                if(i < s.length()-1 && s[i+1] == '1')
                    ans += count;
                if(i == s.length()-1)
                    ans += count;
            }
        }
        return ans;
    }
};
