class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int count=0;
        int x = 1;
        int a = 0;
        for(int i=n-2; i>=0; i--){
            if(s[i]=='1'){
                if(s[i+1]=='1'){
                    count += a;
                }
                else{
                    count += x;
                    a = x;
                    x++;
                }
            }
        }
        return count;
    }
};
