class Solution {
public:
    int maxOperations(string s) {
     int totalOperations = 0;
        int onesCounter = 0;  
        bool oneFound = false;   
        std::vector<int> transitions; 


        for (int i = 0; i < s.length(); i++) {
            if (s[i] == '1') {
                transitions.push_back(1); 
                oneFound = true; 
            }
            if (s[i] == '0' && oneFound) {
                transitions.push_back(0); 
                oneFound = false; 
            }
        }


        for (int segment : transitions) {
            if (segment == 1) {
                onesCounter++; 
            } else {
                totalOperations += onesCounter; 
            }
        }

        return totalOperations;
    }
};
