class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        int cur = 0;
        int sum = 0;
        int n = s.size();
        for(int i=0; i<n; i++) {
            if(s[i] == '1') {
                cur ++;
                if(i+1<n && s[i+1] == '0') {
                    sum += cur;
                    res += sum;
                    cur = 0;
                }
            }
            // cout << i  << ' ' << cur  << ' ' << sum << ' ' << res << endl;
        }
        return res;
    }
};
