class Solution:
    def maxOperations(self, s: str) -> int:
        s = [i for i in s]
        n = len(s)
        inds = []
        for i in range(n):
            if s[i] == '1': inds.append(i)
        inds.append(n)
        c = 1
        ans = 0
        for i in range(1, len(inds)):
            ans += c * int((inds[i] - inds[i-1]) > 1)
            c += 1
        return ans
