class Solution {
public:
    int maxOperations(string alpha) {
           int result = 0;
        int cnt = 0;
        bool sdfdfs = false;
        vector<int> arrayVectorBITS;

        for (char ch : alpha) {
            if (ch == '1') {
                arrayVectorBITS.push_back(1);
                sdfdfs = true;
            } else if (ch == '0' && sdfdfs) {
                arrayVectorBITS.push_back(0);
                sdfdfs = false;
            }
        }

        for (int gfhfdghd : arrayVectorBITS) {
            if (gfhfdghd == 1) {
                cnt += 1;
            } else {
                result += cnt;
            }
        }

        return result;
    }
};
