class Solution {
public:
    int maxOperations(string st) {
        long long ans=0;
        int z=0;
        string s="";
        s=s+st[0];
        for(int i=1;i<st.size();i++){
            if(st[i]=='0' && st[i-1]=='0')
            continue;
            else
            s+=st[i];
        }

int n=s.size();
        for(int i=0;i<s.size();i++){
            if(s[n-i-1]=='1'){
                ans+=z;
            }
            else{
                z++;
            }
        }
        return ans;
    }
};
