class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int i=0;
        int onecnt=0;
        while(i<s.size()){

            int zerocnt=0;
           while(i<s.size()&&s[i]=='1'){
            onecnt++;
             i++;
           }
           while(i<s.size()&&s[i]=='0'){
            zerocnt++;
            i++;
           }
           if(zerocnt>0)
           ans+=onecnt;

        }
        return ans;
    }
};
