class Solution:
    def maxOperations(self, s: str) -> int:
        pos_list = []
        for i in range(len(s)):
            if s[i]=="1":
                pos_list.append(i)

        out_list = self.group(pos_list)
        #print(pos_list)
        #print(out_list)
        all_move = len(out_list)-1
        ans = 0
        tmp = 0
        for i in range(len(out_list)):
            tmp += out_list[i][1]
            ans += out_list[i][1]*(all_move-i)
        if out_list and out_list[-1][0]<len(s)-1:
            ans += tmp

        return ans

    def group(self,pos_list):
        cur_pos = 0
        out_list = []
        while cur_pos<=len(pos_list)-1:
            next_pos = cur_pos+1
            count = 1
            while next_pos <= len(pos_list)-1:
                if pos_list[cur_pos]==pos_list[next_pos]-1:
                    cur_pos += 1
                    next_pos += 1
                    count += 1
                else:
                    break
            out_list.append([pos_list[cur_pos],count])
            cur_pos = next_pos
        return out_list
